import React, { useEffect, useState, useCallback } from "react";
import { Line } from "react-chartjs-2";
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from "chart.js";
import Papa from "papaparse";

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const AccountOwnershipChart = () => {
  const [chartData, setChartData] = useState(null);
  const [error, setError] = useState(null);
  const [countries, setCountries] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState("");
  const [fullData, setFullData] = useState([]); // Store the full dataset

  // Function to process CSV data for a selected country
  const processChartData = useCallback((data, country) => {
    if (!data || data.length === 0) return;

    const countryData = data.filter((row) => row.Country === country && row["Short Indicator"]);

    const transformedData = {};
    countryData.forEach((row) => {
      const indicator = row["Short Indicator"];
      Object.keys(row).forEach((key) => {
        if (!isNaN(key)) { // If column is a year
          if (!transformedData[indicator]) {
            transformedData[indicator] = {};
          }
          transformedData[indicator][key] = row[key];
        }
      });
    });

    if (Object.keys(transformedData).length === 0) {
      setError(`No data available for ${country}`);
      setChartData(null);
      return;
    }

    setError(null); // Clear previous errors
    const years = Object.keys(transformedData[Object.keys(transformedData)[0]] || {});
    const datasets = Object.keys(transformedData).map((indicator) => ({
      label: indicator,
      data: years.map((year) => transformedData[indicator][year] || null),
      borderColor: getRandomColor(),
      fill: false,
    }));

    setChartData({
      labels: years,
      datasets,
    });
  }, []);

  useEffect(() => {
    fetch("/Cleaned_WDIEXCEL.csv")
      .then((response) => response.text())
      .then((csvData) => {
        Papa.parse(csvData, {
          header: true,
          dynamicTyping: true,
          complete: (result) => {
            const data = result.data;
            setFullData(data); // Store the entire dataset

            // Extract unique country names
            const uniqueCountries = [...new Set(data.map((row) => row.Country))].filter(Boolean);
            setCountries(uniqueCountries);

            // Default to Malaysia if it exists, otherwise use the first available country
            const defaultCountry = uniqueCountries.includes("Malaysia") ? "Malaysia" : uniqueCountries[0] || "";
            setSelectedCountry(defaultCountry);

            if (defaultCountry) {
              processChartData(data, defaultCountry);
            }
          },
          error: (parseError) => {
            console.error("Error parsing CSV:", parseError);
            setError(`Error parsing CSV: ${parseError.message}`);
          },
        });
      })
      .catch((fetchError) => {
        console.error("Error fetching CSV:", fetchError);
        setError(`Error fetching CSV: ${fetchError.message}`);
      });
  }, [processChartData]); // Ensures processChartData is properly included

  const getRandomColor = () => {
    return `hsl(${Math.random() * 360}, 70%, 50%)`;
  };

  return (
    <div>
      <h2>Account Ownership by Demographic Group</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}

      <label>Select Country: </label>
      <select onChange={(e) => { 
        const newCountry = e.target.value;
        setSelectedCountry(newCountry); 
        processChartData(fullData, newCountry); // Pass fullData, not chartData
      }} value={selectedCountry}>
        {countries.map((country) => (
          <option key={country} value={country}>{country}</option>
        ))}
      </select>

      {chartData ? <Line data={chartData} /> : <p>Loading...</p>}
    </div>
  );
};

export default AccountOwnershipChart;
