import React, { useEffect, useState } from "react";
import { Line } from "react-chartjs-2";
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from "chart.js";
import Papa from "papaparse";

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const indicatorMapping = {
  "Account ownership at a financial institution or with a mobile-money-service provider, female (% of population ages 15+)": "Female",
  "Account ownership at a financial institution or with a mobile-money-service provider, male (% of population ages 15+)": "Male",
  "Account ownership at a financial institution or with a mobile-money-service provider, older adults (% of population ages 25+)": "Older Adults",
  "Account ownership at a financial institution or with a mobile-money-service provider, poorest 40% (% of population ages 15+)": "Poorest",
  "Account ownership at a financial institution or with a mobile-money-service provider, primary education or less (% of population ages 15+)": "Primary Education or Less",
  "Account ownership at a financial institution or with a mobile-money-service provider, richest 60% (% of population ages 15+)": "Richest",
  "Account ownership at a financial institution or with a mobile-money-service provider, secondary education or more (% of population ages 15+)": "Secondary Education or More",
  "Account ownership at a financial institution or with a mobile-money-service provider, young adults (% of population ages 15-24)": "Young Adults",
};

const AccountOwnershipChart = () => {
  const [chartData, setChartData] = useState(null);

  useEffect(() => {
    fetch("C:/Users/user/Downloads/Paynet Case Study/backend/cleaned_excel_sheet/Cleaned_WDIEXCEL.csv")
      .then((response) => response.text())
      .then((csvData) => {
        Papa.parse(csvData, {
          header: true,
          dynamicTyping: true,
          complete: (result) => {
            const filteredData = result.data.filter((row) => indicatorMapping[row["Indicator Name"]]);
            const transformedData = {};

            filteredData.forEach((row) => {
              const year = row["Year"];
              const indicator = indicatorMapping[row["Indicator Name"]];
              const value = row["Value"];
              
              if (!transformedData[indicator]) {
                transformedData[indicator] = {};
              }
              transformedData[indicator][year] = value;
            });

            const years = Object.keys(transformedData[Object.keys(transformedData)[0]]);
            const datasets = Object.keys(transformedData).map((indicator) => ({
              label: indicator,
              data: years.map((year) => transformedData[indicator][year] || null),
              borderColor: getRandomColor(),
              fill: false,
            }));

            setChartData({
              labels: years,
              datasets,
            });
          },
        });
      });
  }, []);

  const getRandomColor = () => {
    return `hsl(${Math.random() * 360}, 70%, 50%)`;
  };

  return (
    <div>
      <h2>Account Ownership by Demographic Group</h2>
      {chartData ? <Line data={chartData} /> : <p>Loading...</p>}
    </div>
  );
};

export default AccountOwnershipChart;
