import React, { useEffect, useState, useCallback } from "react";
import { Line } from "react-chartjs-2";
import { Chart as ChartJS, LineElement, PointElement, LinearScale, Title, Tooltip, Legend, CategoryScale } from "chart.js";
import Papa from "papaparse";

ChartJS.register(LineElement, PointElement, LinearScale, CategoryScale, Title, Tooltip, Legend);

const DigitalPaymentsChart = () => {
  const [chartData, setChartData] = useState(null);
  const [error, setError] = useState(null);
  const [countries, setCountries] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState("Malaysia");
  const [fullData, setFullData] = useState([]);

  const processChartData = useCallback((data, country) => {
    if (!data || data.length === 0) {
      setError(`No data available for ${country}`);
      setChartData(null);
      return;
    }

    const countryData = data.find(row => row.Country === country);
    if (!countryData) {
      setError(`No data found for ${country}`);
      setChartData(null);
      return;
    }

    setError(null);
    
    const years = ["2011", "2014", "2017", "2021", "2022"];
    const values = years.map(year => countryData[year] || null); // Keep null for gaps in data

    setChartData({
      labels: years,
      datasets: [
        {
          label: "Digital Payments (% of Population Age 15+)",
          data: values,
          borderColor: "#007bff",
          backgroundColor: "rgba(0, 123, 255, 0.2)",
          pointRadius: 5,
          pointHoverRadius: 7,
          fill: true,
          tension: 0.3,
        }
      ]
    });
  }, []);

  useEffect(() => {
    fetch("/Cleaned_FINDEX.csv")
      .then(response => response.text())
      .then(csvData => {
        Papa.parse(csvData, {
          header: true,
          dynamicTyping: true,
          complete: (result) => {
            const data = result.data.filter(row => row["Short Indicator"] === "Digital Payments (% 15+)");
            setFullData(data);
            
            const uniqueCountries = [...new Set(data.map(row => row.Country))].filter(Boolean);
            setCountries(uniqueCountries);
            
            processChartData(data, selectedCountry);
          },
          error: (parseError) => {
            setError(`Error parsing CSV: ${parseError.message}`);
          }
        });
      })
      .catch(fetchError => {
        setError(`Error fetching CSV: ${fetchError.message}`);
      });
  }, [processChartData, selectedCountry]);

  return (
    <div className="max-w-2xl mx-auto p-6 bg-white shadow-lg rounded-xl border border-gray-200">
      <h2 className="text-2xl font-semibold text-gray-800 mb-5 text-center">
        ðŸ“Š Digital Payments Adoption
      </h2>
      
      {error && <p className="text-red-500 font-medium text-center">{error}</p>}
      
      <div className="flex justify-center mb-5">
        <label className="font-medium text-md mr-3">Select Country:</label>
        <select
          className="border rounded-lg px-3 py-1.5 bg-gray-100 hover:bg-gray-200 focus:ring-2 focus:ring-blue-400 transition"
          onChange={(e) => setSelectedCountry(e.target.value)}
          value={selectedCountry}
        >
          {countries.map((country) => (
            <option key={country} value={country}>{country}</option>
          ))}
        </select>
      </div>
      
      {chartData ? (
        <div className="p-3 bg-gray-50 rounded-lg shadow-inner">
          <Line
            data={chartData}
            options={{
              responsive: true,
              maintainAspectRatio: false,
              plugins: {
                legend: {
                  display: true,
                  position: "top",
                },
                tooltip: {
                  callbacks: {
                    label: (tooltipItem) => `${tooltipItem.raw}%`
                  }
                }
              },
              scales: {
                x: {
                  title: {
                    display: true,
                    text: "Year",
                    font: { size: 14, weight: "bold" },
                    color: "#333",
                  }
                },
                y: {
                  title: {
                    display: true,
                    text: "Percentage of Population (%)",
                    font: { size: 14, weight: "bold" },
                    color: "#333",
                  },
                  suggestedMin: 0,
                  suggestedMax: 100
                }
              }
            }}
          />
        </div>
      ) : (
        <p className="text-gray-500 text-center mt-4">ðŸ“¡ Loading data...</p>
      )}
    </div>
  );
};

export default DigitalPaymentsChart;
